changeSet.createProcedure:
BEGIN
MERGE INTO utl_current_version USING ( SELECT 'Maintenix' AS component_name, '@version@' AS version, @servicepack.ver@ AS servicepack_ver, @update.ver@ AS update_ver, '@short.version.name@' AS short_version_name, '@full.version.name@' AS full_version_name, SUBSTR('@build.label@',1,100) AS build_label, SUBSTR('@build.location@',1,80) AS build_location, SUBSTR('@build.revision@',1,50) AS build_revision, SYSDATE AS install_dt, 'Database Upgrade' AS install_sdesc FROM dual ) target_ver ON ( utl_current_version.component_name = target_ver.component_name ) WHEN MATCHED THEN UPDATE SET version = target_ver.version, servicepack_ver = target_ver.servicepack_ver, update_ver = target_ver.update_ver, short_version_name = target_ver.short_version_name, full_version_name = target_ver.full_version_name, build_label = target_ver.build_label, build_location = target_ver.build_location, build_revision = target_ver.build_revision, install_dt = SYSDATE, install_sdesc = 'Database Upgrade' WHEN NOT MATCHED THEN INSERT ( component_name, version, servicepack_ver, update_ver, short_version_name, full_version_name, build_label, build_location, build_revision, install_dt, install_sdesc ) VALUES ( target_ver.component_name, target_ver.version, target_ver.servicepack_ver, target_ver.update_ver, target_ver.short_version_name, target_ver.full_version_name, target_ver.build_label, target_ver.build_location, target_ver.build_revision, target_ver.install_dt, target_ver.install_sdesc );
END;
